jsonSchema:
{
  "title": "1.填寫/修改新案申請書",
  "type": "object",
  "properties": {
    "title": {
      "type": "string",
      "title": "1-1.試驗中文名稱"
    },
    "engTitle": {
      "type": "string",
      "title": "1-2.試驗英文名稱"
    },
    "protocolNum": {
      "type": "string",
      "title": "1-3.試驗編號"
    },
    "protocolVersion": {
      "type": "string",
      "title": "1-4.版本"
    },
    "irbNum": {
      "type": "string",
      "title": "1-5.IRB編號"
    },
    "crcNum": {
      "type": "string",
      "title": "1-6.CRC編號"
    },
    "2-1.IRB核准日期": {
      "type": "string",
      "format": "date"
    },
    "irbApprovedNum": {
      "type": "string",
      "title": "2-2.IRB核准編號"
    },
    "dohFlg": {
      "type": "boolean",
      "title": "2-3.",
      "enumNames": [
        "本試驗已通過衛福部審核",
        "本試驗不需送衛福部審核"
      ]
    },
    "2-4.衛福部核准日期": {
      "type": "string",
      "format": "date"
    },
    "dohApprovedNum": {
      "type": "string",
      "title": "2-5.衛福部核准編號"
    },
    "dohReason": {
      "type": "string",
      "title": "原因"
    },
    "PI": {
      "type": "array",
      "title": "3-1. 試驗主持人",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "title": "姓名"
          },
          "title": {
            "type": "string",
            "title": "職稱"
          },
          "branch": {
            "type": "string",
            "title": "單位"
          },
          "phone": {
            "type": "string",
            "title": "電話"
          },
          "email1": {
            "type": "string",
            "title": "Email1"
          },
          "email2": {
            "type": "string",
            "title": "Email2"
          }
        }
      }
    },
    "LAIs": {
      "type": "array",
      "title": "3-2. 共同主持人",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "title": "姓名"
          },
          "title": {
            "type": "string",
            "title": "職稱"
          },
          "branch": {
            "type": "string",
            "title": "單位"
          },
          "phone": {
            "type": "string",
            "title": "電話"
          },
          "email1": {
            "type": "string",
            "title": "Email1"
          },
          "email2": {
            "type": "string",
            "title": "Email2"
          }
        }
      }
    },
    "AIs": {
      "type": "array",
      "title": "3-3. 協同主持人",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "title": "姓名"
          },
          "title": {
            "type": "string",
            "title": "職稱"
          },
          "branch": {
            "type": "string",
            "title": "單位"
          },
          "phone": {
            "type": "string",
            "title": "電話"
          },
          "email1": {
            "type": "string",
            "title": "Email1"
          },
          "email2": {
            "type": "string",
            "title": "Email2"
          }
        }
      }
    },
    "researchType": {
      "type": "boolean",
      "title": "4.研究類型",
      "enumNames": [
        "一般學術研究/發表",
        "人體試驗"
      ]
    },
    "trialCategory": {
      "title": " ",
      "type": "string",
      "enum": [
        "tech",
        "drug",
        "device",
        "BA/BE"
      ],
      "enumNames": [
        "1. 新醫療技術",
        "2. 新藥品",
        "3. 新醫療器材",
        "4. 學名藥BA/BE"
      ]
    },
    "drugOptions": {
      "title": " ",
      "type": "string",
      "enum": [
        "component",
        "efficiency",
        "useway"
      ],
      "enumNames": [
        "2.1 新成份",
        "2.2 新療效複方",
        "2.3 新使用途徑"
      ]
    },
    "efficiencyOptions": {
      "title": " ",
      "type": "string",
      "enum": [
        "newIndications",
        "sideEffects",
        "strength",
        "time",
        "dose",
        "compound"
      ],
      "enumNames": [
        "2.2.1 新適應症",
        "2.2.2 降低副作用",
        "2.2.3 改善療效強度",
        "2.2.4 改善療效時間",
        "2.2.5 改變使用劑量",
        "2.2.6 複方"
      ]
    },
    "phase": {
      "title": " ",
      "type": "string",
      "enum": [
        "Phase0",
        "PhaseI",
        "PhaseI/II",
        "PhaseII",
        "PhaseII/III",
        "PhaseIII",
        "PhaseIV"
      ],
      "enumNames": [
        "A. Phase 0",
        "B. Phase I",
        "C. Phase I / II",
        "D. Phase II",
        "E. Phase II / III",
        "F. Phase III",
        "G. Phase IV (PMS)"
      ]
    },
    "studyType": {
      "title": "5-1. 計畫型態",
      "type": "string",
      "enum": [
        "world",
        "Taiwan",
        "N/A"
      ],
      "enumNames": [
        "First in World",
        "First in Taiwan",
        "不適用"
      ]
    },
    "locationType": {
      "title": "5-2.",
      "type": "string",
      "enum": [
        "multiCountry",
        "multi",
        "one"
      ],
      "enumNames": [
        "多國多中心",
        "單國多中心",
        "單一中心"
      ]
    },
    "accrualHospital": {
      "type": "string",
      "title": "6.試驗預計收納受試者",
      "description": "本院"
    },
    "accrualGlobal": {
      "type": "string",
      "title": "全球"
    },
    "accrualTaiwan": {
      "type": "string",
      "title": "臺灣"
    },
    "genderEnum": {
      "type": "string",
      "title": "6-2性別",
      "enum": [
        "男",
        "女",
        "性別不拘"
      ]
    },
    "accrualType": {
      "type": "string",
      "title": "6-3類型",
      "enum": [
        "健康受試者",
        "病人"
      ]
    },
    "proposedStartDate": {
      "type": "string",
      "format": "date",
      "title": "7.試驗預計開始執行時間"
    },
    "proposedEndDate": {
      "type": "string",
      "format": "date",
      "title": "7.試驗預計結束時間"
    },
    "locations": {
      "type": "array",
      "title": "8. 執行試驗場所(可複選)",
      "items": {
        "type": "string",
        "enum": [
          "本院臨床試驗中心 (含汀洲院區臨床試驗中心)",
          "門診處",
          "病房",
          "其他"
        ]
      },
      "uniqueItems": true
    },
    "funds": {
      "type": "array",
      "title": "9. 試驗經費來源",
      "items": {
        "type": "string",
        "enum": [
          "廠商",
          "學術研究單位",
          "自籌(自行研究無經費補助，請說明如何自籌)",
          "其他(如美國聯邦政府，請註明單位)"
        ]
      },
      "uniqueItems": true
    },
    "sponsorName": {
      "type": "string",
      "title": "10. 試驗委託藥廠/贊助研究單位名稱",
      "description": "名稱"
    },
    "sponsorAddress": {
      "type": "string",
      "title": "11. 試驗委託藥廠/贊助研究單位地址",
      "description": "地址"
    },
    "croName": {
      "type": "string",
      "title": "11. 試驗委託CRO公司名稱",
      "description": "名稱"
    },
    "croAddress": {
      "type": "string",
      "title": "12. 試驗委託CRO公司地址",
      "description": "地址"
    },
    "insuranceFlg": {
      "type": "string",
      "title": "13. 試驗有無保險",
      "enum": [
        "有",
        "無"
      ]
    },
    "insuranceInst": {
      "type": "string",
      "description": "承保公司"
    },
    "insuranceAmount": {
      "type": "string",
      "description": "保險金額"
    },
    "nihFlg": {
      "type": "string",
      "title": "14. 試驗是否至美國NIH網站登入",
      "enum": [
        "是",
        "否"
      ]
    },
    "nihReason": {
      "type": "string",
      "description": "原因"
    },
    "contractFlg": {
      "type": "string",
      "title": "15. 是否需要簽合約",
      "enum": [
        "是",
        "否"
      ]
    },
    "contractStartDate": {
      "type": "string",
      "format": "date",
      "title": "合約開始日期"
    },
    "contractEndDate": {
      "type": "string",
      "format": "date",
      "title": "合約截止日期"
    },
    "contractAmount": {
      "type": "string",
      "title": "合約核定金額"
    },
    "requiredFiles": {
      "title": "2.上傳/修改新案審查文件",
      "type": "object",
      "properties": {
        "applicationForm": {
          "type": "string",
          "format": "data-url",
          "title": "1. 三軍總醫院臨床研究申請表"
        },
        "approvedLetter": {
          "type": "string",
          "format": "data-url",
          "title": "2. 三軍總醫院IRB審議會同意函"
        },
        "icf": {
          "type": "string",
          "format": "data-url",
          "title": "3. 受試者同意書 (經IRB核准蓋章之影本)"
        },
        "protocolFile": {
          "type": "string",
          "format": "data-url",
          "title": "4. 三軍總醫院人體試驗主持人試驗計畫申請書"
        },
        "dohLetter": {
          "type": "string",
          "format": "data-url",
          "title": "5. 衛福部人體試驗同意函（如須衛福部審查通過案）"
        },
        "protocolInfoFile": {
          "type": "string",
          "format": "data-url",
          "title": "6. 試驗計畫書"
        },
        "caseReport": {
          "type": "string",
          "format": "data-url",
          "title": "7. 個案報告書"
        },
        "insuranceFile": {
          "type": "string",
          "format": "data-url",
          "title": "8. 保險資料"
        },
        "contractFile": {
          "type": "string",
          "format": "data-url",
          "title": "9. 臨床試驗合約書"
        },
        "funding": {
          "type": "string",
          "format": "data-url",
          "title": "10. 臨床試驗經費明細表"
        },
        "fee": {
          "type": "string",
          "format": "data-url",
          "title": "11. 臨床試驗中心服務費收費表 (健康受試者/病人受試者)"
        },
        "otherFile": {
          "type": "string",
          "format": "data-url",
          "title": "12. 其他重要補充資料"
        }
      }
    },
    "preview": {
      "title": "3. 預覽並送出至執行案件區",
      "type": "object",
      "properties": {
        "piAccountFlg": {
          "type": "boolean",
          "title": "是否一併開啟試驗主持人帳號 (如下列，請注意Email必須為院內) ?"
        },
        "lastNamePI": {
          "type": "string",
          "title": "姓名"
        },
        "titlePI": {
          "type": "string",
          "title": "職稱"
        },
        "branchPI": {
          "type": "string",
          "title": "單位"
        },
        "telephonePI": {
          "type": "string",
          "title": "聯絡電話",
          "minLength": 8
        },
        "emailPI": {
          "type": "string",
          "format": "email",
          "title": "院內E-mail"
        }
      }
    }
  }
}

jsonUiSchema:
{
  "title": {
    "ui:readonly": true
  },
  "engTitle": {
    "ui:readonly": true
  },
  "protocolVersion": {
    "ui:readonly": true
  },
  "irbNum": {
    "ui:readonly": true
  },
  "2-1.IRB核准日期": {
    "ui:readonly": true
  },
  "irbApprovedNum": {
    "ui:readonly": true
  },
  "accrualHospital": {
    "ui:readonly": true
  },
  "accrualGlobal": {
    "ui:readonly": true
  },
  "accrualTaiwan": {
    "ui:readonly": true
  },
  "genderEnum": {
    "ui:readonly": true
  },
  "accrualType": {
    "ui:readonly": true
  },
  "proposedStartDate": {
    "ui:readonly": true
  },
  "proposedEndDate": {
    "ui:readonly": true
  },
